varying float4    factor : FACTOR,
fragout float4    fcolor : COLOR)
{
lowpf4 fcolor0 = tex2D(tex[0], tc0);
lowpf4 fcolor1 = tex2D(tex[1], tc1);
fcolor = lerp(fcolor1, fcolor0, factor.r);
}

