Vertex (
uniform   float4   texgen[6],
attribute float4   pos   : POSITION,
varying   float2   tc0 : TEXCOORD,
varying   float2   tc1 : TEXCOORD,
varying   float2   tc2 : TEXCOORD)
{
tc0.x = dot(pos, texgen[0]);
tc0.y = dot(pos, texgen[1]);
